import React, { useState } from 'react';
import { Card, Button, Row, Col, Form, InputGroup, Modal, Badge } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faUsers, 
  faSearch, 
  faPlus, 
  faHeart, 
  faComment, 
  faUpload,
  faUser,
  faClock,
  faUtensils
} from '@fortawesome/free-solid-svg-icons';
import { toast } from 'react-toastify';
import './CommunityRecipes.css';

const CommunityRecipes = () => {
  const [communityRecipes, setCommunityRecipes] = useState([
    {
      id: 1,
      title: 'Homemade Margherita Pizza',
      description: 'A classic Italian pizza with fresh ingredients that anyone can make at home.',
      ingredients: [
        '2 cups all-purpose flour', 
        '1 tsp salt', 
        '3/4 cup warm water', 
        '1 tbsp olive oil',
        '1 can crushed tomatoes',
        '2 cups fresh mozzarella',
        'Fresh basil leaves',
        'Salt and pepper to taste'
      ],
      steps: [
        'Mix flour and salt, then add water and oil to form a dough',
        'Knead for 10 minutes, then rest for 1 hour',
        'Roll out dough and add toppings',
        'Bake at 500°F for 10-12 minutes'
      ],
      imageUrl: 'https://images.unsplash.com/photo-1604382354936-07c5d9983bd3?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80',
      author: 'Maria Rodriguez',
      authorId: 101,
      likes: 24,
      comments: [
        { id: 1, user: 'John', text: 'Made this last night and it was delicious!' },
        { id: 2, user: 'Sarah', text: 'Great recipe! I added some oregano too.' }
      ],
      date: '2023-08-10'
    },
    {
      id: 2,
      title: 'Thai Green Curry',
      description: 'A fragrant and spicy Thai curry that\'s perfect for weeknight dinners.',
      ingredients: [
        '2 tbsp green curry paste', 
        '1 can coconut milk', 
        '500g chicken breast', 
        'Vegetables of choice',
        'Fish sauce',
        'Palm sugar',
        'Thai basil leaves'
      ],
      steps: [
        'Fry curry paste in oil until fragrant',
        'Add coconut milk and bring to simmer',
        'Add chicken and cook until done',
        'Add vegetables and simmer until tender',
        'Season with fish sauce and sugar',
        'Garnish with Thai basil'
      ],
      imageUrl: 'https://images.unsplash.com/photo-1455619452474-d2be8b1e70cd?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80',
      author: 'Michael Chen',
      authorId: 102,
      likes: 18,
      comments: [
        { id: 3, user: 'Lisa', text: 'Loved how easy this was to make!' }
      ],
      date: '2023-08-15'
    },
    {
      id: 3,
      title: 'Classic Chocolate Chip Cookies',
      description: 'Soft and chewy chocolate chip cookies just like grandma used to make.',
      ingredients: [
        '2 1/4 cups all-purpose flour', 
        '1 tsp baking soda', 
        '1 cup butter, softened', 
        '3/4 cup granulated sugar',
        '3/4 cup brown sugar',
        '2 eggs',
        '2 tsp vanilla extract',
        '2 cups chocolate chips'
      ],
      steps: [
        'Preheat oven to 375°F',
        'Mix dry ingredients in a bowl',
        'Cream butter and sugars, then add eggs and vanilla',
        'Combine with dry ingredients',
        'Fold in chocolate chips',
        'Drop tablespoons of dough onto baking sheet',
        'Bake for 9-11 minutes'
      ],
      imageUrl: 'https://images.unsplash.com/photo-1499636136210-6f4ee915583e?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80',
      author: 'Emily Johnson',
      authorId: 103,
      likes: 32,
      comments: [
        { id: 4, user: 'David', text: 'These are the best cookies I\'ve ever made!' },
        { id: 5, user: 'Amanda', text: 'Perfect balance of chewy and crispy.' },
        { id: 6, user: 'Robert', text: 'My kids loved these!' }
      ],
      date: '2023-08-05'
    }
  ]);

  const [searchTerm, setSearchTerm] = useState('');
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [showRecipeModal, setShowRecipeModal] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [newComment, setNewComment] = useState('');

  const [newRecipe, setNewRecipe] = useState({
    title: '',
    description: '',
    ingredients: '',
    steps: '',
    imageUrl: ''
  });

  const filteredRecipes = communityRecipes.filter(recipe =>
    recipe.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    recipe.description.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleViewRecipe = (recipe) => {
    setSelectedRecipe(recipe);
    setShowRecipeModal(true);
  };

  const handleLikeRecipe = (id) => {
    setCommunityRecipes(
      communityRecipes.map(recipe =>
        recipe.id === id ? { ...recipe, likes: recipe.likes + 1 } : recipe
      )
    );
    toast.success('Recipe liked!');
  };

  const handleAddComment = () => {
    if (newComment.trim() === '') {
      toast.error('Comment cannot be empty');
      return;
    }

    const updatedRecipe = {
      ...selectedRecipe,
      comments: [
        ...selectedRecipe.comments,
        { id: Date.now(), user: 'You', text: newComment }
      ]
    };

    setCommunityRecipes(
      communityRecipes.map(recipe =>
        recipe.id === selectedRecipe.id ? updatedRecipe : recipe
      )
    );

    setSelectedRecipe(updatedRecipe);
    setNewComment('');
    toast.success('Comment added!');
  };

  const handleUploadRecipe = () => {
    // Validate form
    if (!newRecipe.title || !newRecipe.description || !newRecipe.ingredients || !newRecipe.steps) {
      toast.error('Please fill in all required fields');
      return;
    }

    // Create new recipe object
    const recipeToAdd = {
      id: Date.now(),
      title: newRecipe.title,
      description: newRecipe.description,
      ingredients: newRecipe.ingredients.split('\n').filter(item => item.trim() !== ''),
      steps: newRecipe.steps.split('\n').filter(item => item.trim() !== ''),
      imageUrl: newRecipe.imageUrl || 'https://images.unsplash.com/photo-1495195134817-aeb325a55b65?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80',
      author: 'You',
      authorId: 999,
      likes: 0,
      comments: [],
      date: new Date().toISOString().split('T')[0]
    };

    // Add to community recipes
    setCommunityRecipes([recipeToAdd, ...communityRecipes]);

    // Reset form and close modal
    setNewRecipe({
      title: '',
      description: '',
      ingredients: '',
      steps: '',
      imageUrl: ''
    });
    setShowUploadModal(false);

    toast.success('Recipe uploaded successfully!');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewRecipe({ ...newRecipe, [name]: value });
  };

  return (
    <div className="community-recipes-container">
      <h2 className="mb-4">
        <FontAwesomeIcon icon={faUsers} className="me-2" />
        Community Recipes
      </h2>

      <div className="d-flex justify-content-between align-items-center mb-4">
        <div className="search-container">
          <InputGroup>
            <InputGroup.Text>
              <FontAwesomeIcon icon={faSearch} />
            </InputGroup.Text>
            <Form.Control
              placeholder="Search community recipes..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </InputGroup>
        </div>

        <Button 
          variant="primary"
          onClick={() => setShowUploadModal(true)}
        >
          <FontAwesomeIcon icon={faUpload} className="me-2" />
          Upload Recipe
        </Button>
      </div>

      {filteredRecipes.length === 0 ? (
        <div className="text-center py-5">
          <p className="text-muted">No recipes found. Try a different search or upload your own!</p>
        </div>
      ) : (
        <Row className="recipes-grid">
          {filteredRecipes.map(recipe => (
            <Col key={recipe.id} lg={4} md={6} className="mb-4">
              <Card className="h-100 recipe-card shadow-sm">
                <div 
                  className="recipe-image"
                  style={{ backgroundImage: `url(${recipe.imageUrl})` }}
                  onClick={() => handleViewRecipe(recipe)}
                ></div>
                <Card.Body>
                  <Card.Title 
                    className="recipe-title"
                    onClick={() => handleViewRecipe(recipe)}
                  >
                    {recipe.title}
                  </Card.Title>
                  <Card.Text className="recipe-description">
                    {recipe.description.length > 100
                      ? `${recipe.description.slice(0, 100)}...`
                      : recipe.description
                    }
                  </Card.Text>
                  <div className="recipe-author">
                    <FontAwesomeIcon icon={faUser} className="me-1" />
                    <span>{recipe.author}</span>
                  </div>
                  <div className="recipe-stats">
                    <div>
                      <Button 
                        variant="outline-danger" 
                        size="sm"
                        className="me-2"
                        onClick={() => handleLikeRecipe(recipe.id)}
                      >
                        <FontAwesomeIcon icon={faHeart} className="me-1" />
                        {recipe.likes}
                      </Button>
                      <Button 
                        variant="outline-secondary" 
                        size="sm"
                        onClick={() => handleViewRecipe(recipe)}
                      >
                        <FontAwesomeIcon icon={faComment} className="me-1" />
                        {recipe.comments.length}
                      </Button>
                    </div>
                    <small className="text-muted">
                      {new Date(recipe.date).toLocaleDateString()}
                    </small>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}

      {/* Upload Recipe Modal */}
      <Modal
        show={showUploadModal}
        onHide={() => setShowUploadModal(false)}
        size="lg"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>Upload Your Recipe</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Recipe Title *</Form.Label>
              <Form.Control
                type="text"
                name="title"
                value={newRecipe.title}
                onChange={handleInputChange}
                placeholder="Enter recipe title"
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Description *</Form.Label>
              <Form.Control
                as="textarea"
                rows={2}
                name="description"
                value={newRecipe.description}
                onChange={handleInputChange}
                placeholder="Briefly describe your recipe"
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Ingredients *</Form.Label>
              <Form.Control
                as="textarea"
                rows={4}
                name="ingredients"
                value={newRecipe.ingredients}
                onChange={handleInputChange}
                placeholder="List ingredients, one per line"
                required
              />
              <Form.Text className="text-muted">
                Enter each ingredient on a new line.
              </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Instructions *</Form.Label>
              <Form.Control
                as="textarea"
                rows={4}
                name="steps"
                value={newRecipe.steps}
                onChange={handleInputChange}
                placeholder="List steps, one per line"
                required
              />
              <Form.Text className="text-muted">
                Enter each step on a new line.
              </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Image URL (optional)</Form.Label>
              <Form.Control
                type="text"
                name="imageUrl"
                value={newRecipe.imageUrl}
                onChange={handleInputChange}
                placeholder="Enter URL for recipe image"
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowUploadModal(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleUploadRecipe}>
            Upload Recipe
          </Button>
        </Modal.Footer>
      </Modal>

      {/* View Recipe Modal */}
      <Modal
        show={showRecipeModal}
        onHide={() => setShowRecipeModal(false)}
        size="lg"
        centered
      >
        {selectedRecipe && (
          <>
            <Modal.Header closeButton>
              <Modal.Title>{selectedRecipe.title}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <div className="recipe-modal-image mb-3">
                <img 
                  src={selectedRecipe.imageUrl} 
                  alt={selectedRecipe.title} 
                  className="img-fluid rounded"
                />
              </div>

              <div className="recipe-modal-info mb-3">
                <div className="author">
                  <FontAwesomeIcon icon={faUser} className="me-1" />
                  <span>{selectedRecipe.author}</span>
                </div>
                <div className="date">
                  <FontAwesomeIcon icon={faClock} className="me-1" />
                  <span>{new Date(selectedRecipe.date).toLocaleDateString()}</span>
                </div>
                <div className="likes">
                  <FontAwesomeIcon icon={faHeart} className="me-1 text-danger" />
                  <span>{selectedRecipe.likes} likes</span>
                </div>
              </div>

              <p className="recipe-modal-description mb-4">{selectedRecipe.description}</p>

              <div className="recipe-modal-content mb-4">
                <h5>
                  <FontAwesomeIcon icon={faUtensils} className="me-2" />
                  Ingredients
                </h5>
                <ul className="ingredients-list">
                  {selectedRecipe.ingredients.map((ingredient, index) => (
                    <li key={index}>{ingredient}</li>
                  ))}
                </ul>
              </div>

              <div className="recipe-modal-content mb-4">
                <h5>Instructions</h5>
                <ol className="steps-list">
                  {selectedRecipe.steps.map((step, index) => (
                    <li key={index}>{step}</li>
                  ))}
                </ol>
              </div>

              <div className="recipe-modal-comments">
                <h5>Comments ({selectedRecipe.comments.length})</h5>
                <div className="comments-section">
                  {selectedRecipe.comments.length > 0 ? (
                    selectedRecipe.comments.map(comment => (
                      <div key={comment.id} className="comment-item">
                        <div className="comment-author">
                          <FontAwesomeIcon icon={faUser} className="me-1" />
                          <span>{comment.user}</span>
                        </div>
                        <div className="comment-text">{comment.text}</div>
                      </div>
                    ))
                  ) : (
                    <p className="text-muted">No comments yet. Be the first to comment!</p>
                  )}
                </div>

                <div className="add-comment-section mt-3">
                  <Form.Control
                    as="textarea"
                    rows={2}
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Add a comment..."
                    className="mb-2"
                  />
                  <Button 
                    variant="primary" 
                    size="sm"
                    onClick={handleAddComment}
                  >
                    Post Comment
                  </Button>
                </div>
              </div>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={() => setShowRecipeModal(false)}>
                Close
              </Button>
              <Button 
                variant="danger" 
                onClick={() => handleLikeRecipe(selectedRecipe.id)}
              >
                <FontAwesomeIcon icon={faHeart} className="me-1" />
                Like
              </Button>
            </Modal.Footer>
          </>
        )}
      </Modal>
    </div>
  );
};

export default CommunityRecipes;
