import React, { useState } from 'react';
import { Card, Form, Button, Row, Col, Spinner, Alert } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCamera, faSearch, faUtensils, faClock, faFire } from '@fortawesome/free-solid-svg-icons';
import { toast } from 'react-toastify';
import api from '../../utils/api';
import './RecipeGenerator.css';

const RecipeGenerator = () => {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [loading, setLoading] = useState(false);
  const [recipes, setRecipes] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [activeTab, setActiveTab] = useState('upload'); // 'upload' or 'search'
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (!selectedFile.type.startsWith('image/')) {
        toast.error('Please select an image file');
        return;
      }

      setFile(selectedFile);

      // Create preview
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(selectedFile);
    }
  };

  const handleImageUpload = async (e) => {
    e.preventDefault();

    if (!file) {
      toast.error('Please select an image to upload');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('image', file);

      const res = await api.post('/api/recipe-generator/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setRecipes(res.data.recipes);
      toast.success('Image processed successfully!');
    } catch (err) {
      console.error('Error uploading image:', err);
      setError(err.response?.data?.msg || 'Error processing your image');
      toast.error('Failed to process image');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchQuery.trim()) {
      toast.error('Please enter a search term');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const res = await api.get(`/api/recipe-generator/search?query=${encodeURIComponent(searchQuery)}`);
      setRecipes(res.data.recipes);

      if (res.data.recipes.length === 0) {
        toast.info('No recipes found matching your search');
      }
    } catch (err) {
      console.error('Error searching recipes:', err);
      setError(err.response?.data?.msg || 'Error searching for recipes');
      toast.error('Failed to search recipes');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="recipe-generator-container">
      <h2 className="mb-4">
        <FontAwesomeIcon icon={faUtensils} className="me-2" />
        Recipe Generator
      </h2>

      <Card className="shadow-sm mb-4">
        <Card.Body>
          <div className="tab-buttons mb-4">
            <Button 
              variant={activeTab === 'upload' ? 'primary' : 'outline-primary'} 
              className="me-2"
              onClick={() => setActiveTab('upload')}
            >
              <FontAwesomeIcon icon={faCamera} className="me-2" />
              Upload Image
            </Button>
            <Button 
              variant={activeTab === 'search' ? 'primary' : 'outline-primary'} 
              onClick={() => setActiveTab('search')}
            >
              <FontAwesomeIcon icon={faSearch} className="me-2" />
              Search by Ingredients
            </Button>
          </div>

          {activeTab === 'upload' ? (
            <Form onSubmit={handleImageUpload}>
              <div className="text-center mb-4">
                <p>Upload a photo of ingredients or a dish to get recipe suggestions</p>
              </div>

              <div className="image-upload-container mb-4">
                {preview ? (
                  <div className="preview-container">
                    <img 
                      src={preview} 
                      alt="Preview" 
                      className="img-preview" 
                    />
                    <Button 
                      variant="outline-danger" 
                      size="sm"
                      className="remove-image-btn"
                      onClick={() => {
                        setFile(null);
                        setPreview(null);
                      }}
                    >
                      Remove
                    </Button>
                  </div>
                ) : (
                  <div className="upload-placeholder">
                    <FontAwesomeIcon icon={faCamera} size="2x" />
                    <p>Click to select an image</p>
                  </div>
                )}
                <input
                  type="file"
                  accept="image/*"
                  className="file-input"
                  onChange={handleFileChange}
                />
              </div>

              <Button 
                variant="primary" 
                type="submit" 
                className="w-100"
                disabled={loading || !file}
              >
                {loading ? (
                  <>
                    <Spinner 
                      as="span" 
                      animation="border" 
                      size="sm" 
                      role="status" 
                      aria-hidden="true" 
                      className="me-2"
                    />
                    Processing...
                  </>
                ) : (
                  <>Generate Recipes</>
                )}
              </Button>
            </Form>
          ) : (
            <Form onSubmit={handleSearch}>
              <div className="text-center mb-4">
                <p>Search for recipes by ingredients or dish name</p>
              </div>

              <Form.Group className="mb-3">
                <Form.Control
                  type="text"
                  placeholder="Enter ingredients or dish name (e.g., chicken, tomatoes, pasta)"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </Form.Group>

              <Button 
                variant="primary" 
                type="submit" 
                className="w-100"
                disabled={loading || !searchQuery.trim()}
              >
                {loading ? (
                  <>
                    <Spinner 
                      as="span" 
                      animation="border" 
                      size="sm" 
                      role="status" 
                      aria-hidden="true" 
                      className="me-2"
                    />
                    Searching...
                  </>
                ) : (
                  <>Search Recipes</>
                )}
              </Button>
            </Form>
          )}
        </Card.Body>
      </Card>

      {error && (
        <Alert variant="danger" className="mb-4">
          {error}
        </Alert>
      )}

      {recipes.length > 0 && (
        <div className="recipe-results">
          <h3 className="mb-3">Recipe Suggestions</h3>

          <Row>
            {recipes.map((recipe, index) => (
              <Col md={6} key={index} className="mb-4">
                <Card className="recipe-card h-100">
                  <Card.Body>
                    <Card.Title className="recipe-title">{recipe.name}</Card.Title>

                    <div className="recipe-meta mb-3">
                      <span className="recipe-time">
                        <FontAwesomeIcon icon={faClock} className="me-1" />
                        {recipe.cooking_time}
                      </span>
                      <span className="recipe-calories">
                        <FontAwesomeIcon icon={faFire} className="me-1" />
                        {recipe.calories}
                      </span>
                    </div>

                    <div className="recipe-section mb-3">
                      <h5>Ingredients</h5>
                      <pre className="recipe-ingredients">{recipe.ingredients}</pre>
                    </div>

                    <div className="recipe-section">
                      <h5>Directions</h5>
                      <pre className="recipe-directions">{recipe.directions}</pre>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </div>
      )}
    </div>
  );
};

export default RecipeGenerator;
