import React, { useState, useRef, useContext } from 'react';
import { Container, Row, Col, Card, Button, Form, Spinner, Alert } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCamera, faUpload, faWandMagicSparkles, faSave, faArrowRight } from '@fortawesome/free-solid-svg-icons';
import AuthContext from '../../context/auth/authContext';
import api from '../../utils/api';
import { toast } from 'react-toastify';
import './RecipeGenerator.css';

const RecipeGenerator = () => {
  const authContext = useContext(AuthContext);
  const { isAuthenticated, user } = authContext;

  const [selectedImage, setSelectedImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [generatedRecipes, setGeneratedRecipes] = useState([]);
  const [moreRecipes, setMoreRecipes] = useState([]);
  const [showMoreRecipes, setShowMoreRecipes] = useState(false);
  const [selectedRecipe, setSelectedRecipe] = useState(null);
  const [error, setError] = useState(null);

  const fileInputRef = useRef(null);
  const cameraInputRef = useRef(null);

  // Handle file selection
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(file);
      setPreviewUrl(URL.createObjectURL(file));
      setGeneratedRecipes([]);
      setMoreRecipes([]);
      setShowMoreRecipes(false);
      setSelectedRecipe(null);
      setError(null);
    }
  };

  // Trigger file input click
  const handleUploadClick = () => {
    fileInputRef.current.click();
  };

  // Trigger camera input click
  const handleCameraClick = () => {
    cameraInputRef.current.click();
  };

  // Generate recipes from the selected image
  const handleGenerateRecipes = async () => {
    if (!selectedImage) {
      toast.error('Please select or take a photo first');
      return;
    }

    setIsProcessing(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('image', selectedImage);

      const response = await api.post('/api/recipe-generator/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data.success) {
        setGeneratedRecipes(response.data.recipes || []);
        setMoreRecipes(response.data.moreRecipes || []);

        if (response.data.recipes.length === 0) {
          toast.info('No recipes found for this image. Try another one!');
        } else {
          toast.success('Recipes generated successfully!');
        }
      } else {
        setError(response.data.msg || 'Failed to generate recipes');
        toast.error('Failed to generate recipes');
      }
    } catch (err) {
      console.error('Error generating recipes:', err);
      setError(err.response?.data?.msg || 'Error processing image');
      toast.error('Error processing image');
    } finally {
      setIsProcessing(false);
    }
  };

  // Save a recipe to the user's collection
  const handleSaveRecipe = async (recipe) => {
    if (!isAuthenticated) {
      toast.error('Please log in to save recipes');
      return;
    }

    try {
      const recipeData = {
        title: recipe.name,
        description: `Generated recipe for ${recipe.name}`,
        ingredients: Array.isArray(recipe.ingredients) 
          ? recipe.ingredients 
          : recipe.ingredients.split('\n').filter(item => item.trim()),
        steps: Array.isArray(recipe.directions) 
          ? recipe.directions 
          : recipe.directions.split('\n').filter(item => item.trim()),
        calories: recipe.calories?.replace('Calories: ', '') || null,
        cookTime: recipe.cooking_time || '',
        servings: recipe.servings || 4,
        imageUrl: recipe.imageUrl || '',
      };

      const response = await api.post('/api/recipe-generator/save', recipeData);

      toast.success('Recipe saved to your collection!');
      setSelectedRecipe(null);
    } catch (err) {
      console.error('Error saving recipe:', err);
      toast.error('Failed to save recipe');
    }
  };

  // View recipe details
  const handleViewRecipe = (recipe) => {
    setSelectedRecipe(recipe);
  };

  // Back to recipe list
  const handleBackToList = () => {
    setSelectedRecipe(null);
  };

  // Toggle showing more recipes
  const handleToggleMoreRecipes = () => {
    setShowMoreRecipes(!showMoreRecipes);
  };

  return (
    <Container className="recipe-generator-container my-4">
      <h2 className="text-center mb-4">
        <FontAwesomeIcon icon={faWandMagicSparkles} className="me-2" />
        Recipe Generator
      </h2>

      <Row className="mb-4">
        <Col md={6} className="mb-3 mb-md-0">
          <Card className="h-100 shadow-sm">
            <Card.Body className="d-flex flex-column">
              <h4 className="mb-3">Upload Food Image</h4>

              <div className="image-upload-container mb-3">
                {previewUrl ? (
                  <div className="preview-container">
                    <img 
                      src={previewUrl} 
                      alt="Preview" 
                      className="img-preview"
                    />
                    <Button 
                      variant="outline-danger" 
                      size="sm" 
                      className="remove-image-btn"
                      onClick={() => {
                        setPreviewUrl(null);
                        setSelectedImage(null);
                      }}
                    >
                      ×
                    </Button>
                  </div>
                ) : (
                  <div className="upload-placeholder">
                    <FontAwesomeIcon icon={faUpload} size="3x" />
                    <p className="mt-2">Select or take a photo of food</p>
                  </div>
                )}
              </div>

              <div className="d-flex justify-content-center mt-auto">
                <Button 
                  variant="outline-primary" 
                  className="me-2"
                  onClick={handleUploadClick}
                >
                  <FontAwesomeIcon icon={faUpload} className="me-2" />
                  Upload Image
                </Button>

                <Button 
                  variant="outline-success"
                  onClick={handleCameraClick}
                >
                  <FontAwesomeIcon icon={faCamera} className="me-2" />
                  Take Photo
                </Button>

                {/* Hidden file inputs */}
                <Form.Control
                  type="file"
                  ref={fileInputRef}
                  className="d-none"
                  accept="image/*"
                  onChange={handleFileChange}
                />

                <Form.Control
                  type="file"
                  ref={cameraInputRef}
                  className="d-none"
                  accept="image/*"
                  capture="environment"
                  onChange={handleFileChange}
                />
              </div>
            </Card.Body>
          </Card>
        </Col>

        <Col md={6}>
          <Card className="h-100 shadow-sm">
            <Card.Body className="d-flex flex-column">
              <h4 className="mb-3">Generate Recipe</h4>

              <p className="text-muted">
                Upload a photo of food and our AI will identify it and suggest recipes.
              </p>

              <ul className="mb-4">
                <li>Clear photos work best</li>
                <li>Center the dish in the frame</li>
                <li>Good lighting improves accuracy</li>
                <li>Avoid multiple dishes in one photo</li>
              </ul>

              {error && (
                <Alert variant="danger" className="mb-3">
                  {error}
                </Alert>
              )}

              <div className="mt-auto">
                <Button 
                  variant="primary" 
                  className="w-100"
                  disabled={!selectedImage || isProcessing}
                  onClick={handleGenerateRecipes}
                >
                  {isProcessing ? (
                    <>
                      <Spinner
                        as="span"
                        animation="border"
                        size="sm"
                        role="status"
                        aria-hidden="true"
                        className="me-2"
                      />
                      Processing...
                    </>
                  ) : (
                    <>
                      <FontAwesomeIcon icon={faWandMagicSparkles} className="me-2" />
                      Generate Recipe
                    </>
                  )}
                </Button>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Recipe Results */}
      {generatedRecipes.length > 0 && !selectedRecipe && (
        <div className="recipe-results mt-4">
          <h3 className="mb-3">Recipe Suggestions</h3>

          <Row>
            {generatedRecipes.map((recipe, index) => (
              <Col md={4} key={index} className="mb-4">
                <Card className="h-100 shadow-sm recipe-card">
                  <Card.Body>
                    <h5 className="recipe-title">{recipe.name}</h5>

                    <div className="recipe-info mb-3">
                      <span className="me-3">
                        <strong>Time:</strong> {recipe.cooking_time || 'N/A'}
                      </span>
                      <span>
                        <strong>Calories:</strong> {recipe.calories?.replace('Calories: ', '') || 'N/A'}
                      </span>
                    </div>

                    <div className="recipe-ingredients mb-3">
                      <strong>Ingredients:</strong>
                      <p className="text-muted ingredients-preview">
                        {Array.isArray(recipe.ingredients) 
                          ? recipe.ingredients.slice(0, 3).join(', ') + (recipe.ingredients.length > 3 ? '...' : '')
                          : recipe.ingredients.split('\n').slice(0, 3).join(', ') + '...'}
                      </p>
                    </div>

                    <div className="d-flex justify-content-between mt-auto">
                      <Button 
                        variant="outline-primary" 
                        size="sm"
                        onClick={() => handleViewRecipe(recipe)}
                      >
                        View Details
                      </Button>

                      <Button 
                        variant="outline-success" 
                        size="sm"
                        onClick={() => handleSaveRecipe(recipe)}
                        disabled={!isAuthenticated}
                      >
                        <FontAwesomeIcon icon={faSave} className="me-1" />
                        Save
                      </Button>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>

          {moreRecipes.length > 0 && (
            <div className="text-center mt-3 mb-5">
              <Button 
                variant="link" 
                onClick={handleToggleMoreRecipes}
              >
                {showMoreRecipes ? 'Show Less' : 'Show More Recipes'}
              </Button>
            </div>
          )}

          {showMoreRecipes && moreRecipes.length > 0 && (
            <Row>
              {moreRecipes.map((recipe, index) => (
                <Col md={4} key={index} className="mb-4">
                  <Card className="h-100 shadow-sm recipe-card">
                    <Card.Body>
                      <h5 className="recipe-title">{recipe.name}</h5>

                      <div className="recipe-info mb-3">
                        <span className="me-3">
                          <strong>Time:</strong> {recipe.cooking_time || 'N/A'}
                        </span>
                        <span>
                          <strong>Calories:</strong> {recipe.calories?.replace('Calories: ', '') || 'N/A'}
                        </span>
                      </div>

                      <div className="recipe-ingredients mb-3">
                        <strong>Ingredients:</strong>
                        <p className="text-muted ingredients-preview">
                          {Array.isArray(recipe.ingredients) 
                            ? recipe.ingredients.slice(0, 3).join(', ') + (recipe.ingredients.length > 3 ? '...' : '')
                            : recipe.ingredients.split('\n').slice(0, 3).join(', ') + '...'}
                        </p>
                      </div>

                      <div className="d-flex justify-content-between mt-auto">
                        <Button 
                          variant="outline-primary" 
                          size="sm"
                          onClick={() => handleViewRecipe(recipe)}
                        >
                          View Details
                        </Button>

                        <Button 
                          variant="outline-success" 
                          size="sm"
                          onClick={() => handleSaveRecipe(recipe)}
                          disabled={!isAuthenticated}
                        >
                          <FontAwesomeIcon icon={faSave} className="me-1" />
                          Save
                        </Button>
                      </div>
                    </Card.Body>
                  </Card>
                </Col>
              ))}
            </Row>
          )}
        </div>
      )}

      {/* Recipe Detail View */}
      {selectedRecipe && (
        <div className="recipe-detail mt-4">
          <div className="d-flex justify-content-between align-items-center mb-3">
            <Button 
              variant="link" 
              className="p-0"
              onClick={handleBackToList}
            >
              <FontAwesomeIcon icon={faArrowRight} rotation={180} className="me-2" />
              Back to results
            </Button>

            <Button 
              variant="success"
              onClick={() => handleSaveRecipe(selectedRecipe)}
              disabled={!isAuthenticated}
            >
              <FontAwesomeIcon icon={faSave} className="me-2" />
              Save Recipe
            </Button>
          </div>

          <Card className="shadow-sm">
            <Card.Body>
              <h3 className="mb-3">{selectedRecipe.name}</h3>

              <Row className="mb-4">
                <Col md={6}>
                  <div className="recipe-meta mb-4">
                    <div className="d-flex mb-2">
                      <div className="me-4">
                        <strong>Cooking Time:</strong> {selectedRecipe.cooking_time || 'N/A'}
                      </div>
                      <div>
                        <strong>Calories:</strong> {selectedRecipe.calories?.replace('Calories: ', '') || 'N/A'}
                      </div>
                    </div>
                  </div>

                  <div className="recipe-ingredients mb-4">
                    <h5>Ingredients</h5>
                    <ul className="ingredient-list">
                      {Array.isArray(selectedRecipe.ingredients) 
                        ? selectedRecipe.ingredients.map((ingredient, i) => (
                            <li key={i}>{ingredient}</li>
                          ))
                        : selectedRecipe.ingredients.split('\n').filter(item => item.trim()).map((ingredient, i) => (
                            <li key={i}>{ingredient}</li>
                          ))
                      }
                    </ul>
                  </div>
                </Col>

                <Col md={6}>
                  <div className="recipe-directions">
                    <h5>Directions</h5>
                    <ol className="direction-list">
                      {Array.isArray(selectedRecipe.directions) 
                        ? selectedRecipe.directions.map((step, i) => (
                            <li key={i}>{step}</li>
                          ))
                        : selectedRecipe.directions.split('\n').filter(item => item.trim()).map((step, i) => (
                            <li key={i}>{step}</li>
                          ))
                      }
                    </ol>
                  </div>
                </Col>
              </Row>
            </Card.Body>
          </Card>
        </div>
      )}
    </Container>
  );
};

export default RecipeGenerator;
